import React from "react"
import { Link } from "react-router-dom"
import { Button, Stack, Heading, Text, Center } from "@chakra-ui/react"
import Confetti from "react-confetti"

export default function EndGame(props) {
  const { displayText, restart, word, gameState } = props;
  // Hook
function useWindowSize() {
  // Initialize state with undefined width/height so server and client renders match
  // Learn more here: https://joshwcomeau.com/react/the-perils-of-rehydration/
  const [windowSize, setWindowSize] = useState({
    width: undefined,
    height: undefined,
  });
  useEffect(() => {
    // Handler to call on window resize
    function handleResize() {
      // Set window width/height to state
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    }
    // Add event listener
    window.addEventListener("resize", handleResize);
    // Call handler right away so state gets updated with initial window size
    handleResize();
    // Remove event listener on cleanup
    return () => window.removeEventListener("resize", handleResize);
  }, []); // Empty array ensures that effect is only run on mount
  return windowSize;
}
  const size = useWindowSize()
  return (
    <>
      <Center mt={5}>
        <Heading>{displayText}</Heading>
      </Center>

      {gameState === 2 ? (
        <Center mt={5}>
          <Text fontSize="lg" color="tomato">
            <b>Word: {word}</b>
          </Text>
        </Center>
      ) : (
        <Confetti
          width={size.width}
          height={size.height}
          numberOfPieces={500}
          recycle={false}
        />
      )}
      <Stack direction="row" mt={5} justify="center">
        <Link to="/">
          <Button colorScheme="teal">Home</Button>
        </Link>
        <Button colorScheme="teal" onClick={restart}>
          Restart
        </Button>
      </Stack>
    </>
  );
}
